BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "age" (
	"age"	TEXT,
	"age_min"	NUMERIC,
	"age_max"	NUMERIC,
	"male_count"	NUMERIC,
	"female_count"	NUMERIC,
	"total_count"	NUMERIC,
	"male"	NUMERIC,
	"female"	NUMERIC,
	"total"	NUMERIC
);
CREATE TABLE IF NOT EXISTS "gender" (
	"gender"	TEXT,
	"count"	NUMERIC,
	"percentage"	NUMERIC
);
CREATE TABLE IF NOT EXISTS "height" (
	"height"	TEXT,
	"male"	NUMERIC,
	"female"	NUMERIC,
	"height_min"	NUMERIC,
	"height_max"	NUMERIC
);
CREATE TABLE IF NOT EXISTS "income" (
	"income_class"	TEXT,
	"percent"	NUMERIC,
	"income_min"	NUMERIC,
	"income_max"	NUMERIC
);
CREATE TABLE IF NOT EXISTS "relationship_status" (
	"age_group"	TEXT,
	"percent_singles_of_population"	NUMERIC,
	"percent_population"	NUMERIC,
	"population_count"	NUMERIC,
	"singles_count"	NUMERIC,
	"percent"	NUMERIC,
	"age_min"	NUMERIC,
	"age_max"	NUMERIC
);
CREATE TABLE IF NOT EXISTS "weight" (
	"age_group"	TEXT,
	"gender"	TEXT,
	"underweight"	NUMERIC,
	"normal"	NUMERIC,
	"overweight"	NUMERIC,
	"obese"	NUMERIC,
	"age_min"	NUMERIC,
	"age_max"	NUMERIC
);
COMMIT;
